INSERT INTO `cd_company` (`company_uid`, `company_tags`, `company_caption`, `company_type`, `company_lang`) VALUES
(1, 'company_name', 'Filantropi Indonesia', '', ''),
(2, 'company_about', 'Filantropi Indonesia merupakan perhimpunan yang independen dan nirlaba beranggotakan organisasi dan jaringan filantropi yang berupaya memajukan kedermawan sosial yang efektif dan berlandaskan etika untuk mewujudkan keadilan sosial dan pembangunan berkelanjutan di Indonesia.', '', ''),
