INSERT INTO `cd_email` (`email_uid`, `email_nickname`, `email_sender`, `email_receiver`, `email_cc`, `email_bcc`, `email_subject`, `email_message`, `email_flag`, `email_lang`, `email_status`, `email_lock_code`) VALUES
(2, 'Wins Property', 'no-reply@administrator.com', 'raeka_mysterio@yahoo.com', 'no-reply@administrator', 'no-reply@administrator', 'Request Form', '<p><strong>Data email masuk</strong></p>\r\n\r\n<p><strong>Property : </strong>[product_name]</p>\r\n\r\n<p><strong>Nama :</strong> [tag_name]</p>\r\n\r\n<p><strong>Company :</strong> [tag_company]</p>\r\n\r\n<p><strong>Email :</strong> [tag_email]</p>\r\n\r\n<p><strong>No. HP : </strong>[tag_mobile]</p>\r\n\r\n<p><strong>Pesan :</strong></p>\r\n\r\n<p>[tag_email_content]</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Terima Kasih</strong></p>\r\n\r\n<p><em>Administrator</em></p>\r\n', 'EMAIL_REQUEST_FORM', '', 'N', ''),
(3, 'Filantropi Indonesia', 'no-reply@filantropi.or.id', 'raeka_mysterio@yahoo.com', 'humas@filantropi.or.id', 'admin@filantropi.or.id', 'Member Data Philanthropy Indonesia', '<p><strong>[tag_logo]</strong></p>\n\n<p style=\"text-align:center\"><span style=\"font-size:20px\"><strong>Member Application Sheet Philanthropy Indonesia</strong></span></p>\n\n<p>Thanks for the interest of your organization to become a member of Philanthropy Indonesia (FI). As a national association of philanthropy activists as well as members of the association and network of global philanthropy, FI works to strengthen, promote and provide a variety of programs and services for the development of effective and sustainable philanthropy in Indonesia. Your membership application will soon be followed by the secretariat lanjutin Philanthropy Indonesia.</p>\n\n<table border=\"1\" cellpadding=\"5\" cellspacing=\"0\" style=\"width:100%\">\n	<thead>\n		<tr>\n			<th colspan=\"2\" scope=\"col\" style=\"text-align:left\"><span style=\"font-size:14px\">Contact Information</span></th>\n		</tr>\n	</thead>\n	<tbody>\n		<tr>\n			<td style=\"width:45%\">Association Name</td>\n			<td style=\"width:55%\">: <strong>[tag_name]</strong></td>\n		</tr>\n		<tr>\n			<td>Name of Executive Director / Chairman of the Foundation / Chairman of the Association / Society&nbsp;</td>\n			<td>:&nbsp;<strong>[tag_director_name]</strong></td>\n		</tr>\n		<tr>\n			<td>Address</td>\n			<td>:&nbsp;<strong>[tag_address]</strong></td>\n		</tr>\n		<tr>\n			<td>Telephone&nbsp;/ Fax</td>\n			<td>:&nbsp;<strong>[tag_telephone]</strong></td>\n		</tr>\n		<tr>\n			<td>Primary Contact Name</td>\n			<td>:&nbsp;<strong>[tag_contact_name]</strong></td>\n		</tr>\n		<tr>\n			<td>Position Primary Contact</td>\n			<td>:&nbsp;<strong>[tag_contact_position]</strong></td>\n		</tr>\n		<tr>\n			<td>Primary Contact Email</td>\n			<td>:&nbsp;<strong>[tag_contact_email]</strong></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<table border=\"1\" cellpadding=\"5\" cellspacing=\"0\" style=\"width:100%\">\n	<thead>\n		<tr>\n			<th scope=\"col\" style=\"text-align:left\"><span style=\"font-size:14px\">Type of Organization</span></th>\n		</tr>\n	</thead>\n	<tbody>\n		<tr>\n			<td style=\"text-align:center\"><strong>[tag_type]</strong></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<table border=\"1\" cellpadding=\"5\" cellspacing=\"0\" style=\"width:100%\">\n	<thead>\n		<tr>\n			<th scope=\"col\" style=\"text-align:left\"><span style=\"font-size:14px\">Members Due</span></th>\n		</tr>\n	</thead>\n	<tbody>\n		<tr>\n			<td style=\"text-align:center\"><strong>[tag_due]</strong></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p><strong>[tag_date]</strong></p>\n\n<p><strong>Signature</strong></p>\n\n<p><strong>[tag_signature]</strong></p>\n', 'EMAIL_REGISTRATION_MEMBER', 'en', 'Y', 'KfiHe8Ihehj8WUj'),
(4, 'Filantropi Indonesia', 'no-reply@filantropi.or.id', 'raeka_mysterio@yahoo.com', 'humas@filantropi.or.id', 'admin@filantropi.or.id', 'Data Anggota Filantropi Indonesia', '<p><strong>[tag_logo]</strong></p>\n\n<p style=\"text-align:center\"><span style=\"font-size:20px\"><strong>Lembar Aplikasi Anggota Filantropi Indonesia</strong></span></p>\n\n<p>Terima kasih atas minat organisasi Anda untuk menjadi anggota Filantropi Indonesia (FI). Sebagai sebuah asosiasi nasional pegiat filantropi serta anggota dari asosiasi dan jaringan filantropi global, FI bekerja untuk memperkuat, mempromosikan dan menyediakan berbagai program dan layanan untuk pengembangan filantropi yang efektif dan berkelanjutan di Indonesia. Permohonan keanggotaan anda akan segera di tindak lanjutin oleh sekretariat Filantropi Indonesia.</p>\n\n<table border=\"1\" cellpadding=\"5\" cellspacing=\"0\" style=\"width:100%\">\n	<thead>\n		<tr>\n			<th colspan=\"2\" scope=\"col\" style=\"text-align:left\"><span style=\"font-size:14px\">Informasi Kontak</span></th>\n		</tr>\n	</thead>\n	<tbody>\n		<tr>\n			<td style=\"width:45%\">Nama lembaga</td>\n			<td style=\"width:55%\">: <strong>[tag_name]</strong></td>\n		</tr>\n		<tr>\n			<td>Nama Direktur Eksekutif /&nbsp;Ketua Yayasan /&nbsp;Ketua Perhimpunan/Perkumpulan&nbsp;</td>\n			<td>:&nbsp;<strong>[tag_director_name]</strong></td>\n		</tr>\n		<tr>\n			<td>Alamat</td>\n			<td>:&nbsp;<strong>[tag_address]</strong></td>\n		</tr>\n		<tr>\n			<td>Telepon / Fax</td>\n			<td>:&nbsp;<strong>[tag_telephone]</strong></td>\n		</tr>\n		<tr>\n			<td>Nama Kontak Utama</td>\n			<td>:&nbsp;<strong>[tag_contact_name]</strong></td>\n		</tr>\n		<tr>\n			<td>Jabatan Kontak Utama</td>\n			<td>:&nbsp;<strong>[tag_contact_position]</strong></td>\n		</tr>\n		<tr>\n			<td>E-mail Kontak Utama</td>\n			<td>:&nbsp;<strong>[tag_contact_email]</strong></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<table border=\"1\" cellpadding=\"5\" cellspacing=\"0\" style=\"width:100%\">\n	<thead>\n		<tr>\n			<th scope=\"col\" style=\"text-align:left\"><span style=\"font-size:14px\">Jenis Organisasi</span></th>\n		</tr>\n	</thead>\n	<tbody>\n		<tr>\n			<td style=\"text-align:center\"><strong>[tag_type]</strong></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<table border=\"1\" cellpadding=\"5\" cellspacing=\"0\" style=\"width:100%\">\n	<thead>\n		<tr>\n			<th scope=\"col\" style=\"text-align:left\"><span style=\"font-size:14px\">Kontribusi Iuran Anggota</span></th>\n		</tr>\n	</thead>\n	<tbody>\n		<tr>\n			<td style=\"text-align:center\"><strong>[tag_due]</strong></td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n\n<p><strong>[tag_date]</strong></p>\n\n<p><strong>Tanda Tangan</strong></p>\n\n<p><strong>[tag_signature]</strong></p>\n', 'EMAIL_REGISTRATION_MEMBER', 'id', 'Y', 'KfiHe8Ihehj8WUj'),
(5, 'Armasparts', 'no-reply@armasparts.com', 'raeka_mysterio@yahoo.com', 'no-reply@armaspart.com', 'no-reply@armaspart.com', 'Order Confirmation', '<table align=\"left\" border=\"0\" cellpadding=\"4\" cellspacing=\"2\" style=\"border:1px solid #CCC; width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"text-align:center\"><span style=\"font-size:18px\"><strong>ORDER DETAIL #[orderid]</strong></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:top\"><strong>Personal Detail :</strong><br />\r\n			[tag_name]<br />\r\n			[tag_email]<br />\r\n			[tag_mobile]</td>\r\n			<td style=\"text-align:right; vertical-align:top\"><strong>Shipping Detail:</strong><br />\r\n			[tag_ship_address]<br />\r\n			&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\">[tag_list_order]</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: right; width: 80%;\">Total Purchase : [tag_currency]</td>\r\n			<td style=\"text-align: right; width: 20px;\">[tag_total_purchase]</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:right\">Voucher Discount (-) : [tag_currency]</td>\r\n			<td style=\"text-align:right\">[tag_vch]</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:right\">Shipping (+) : [tag_currency]</td>\r\n			<td style=\"text-align:right\">[tag_shipping]</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:right\"><strong>Grand Total : </strong>[tag_currency]</td>\r\n			<td style=\"text-align:right\"><strong>[tag_grand_total]</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Thank&#39;s For Order</strong></p>\r\n\r\n<p><strong>Armaspart</strong></p>\r\n', 'EMAIL_ORDER_CONFIRMATION', 'en', 'N', ''),
(6, 'Armasparts', 'no-reply@armasparts.com', 'raeka_mysterio@yahoo.com', 'no-reply@armaspart.com', 'no-reply@armaspart.com', 'Konfirmasi Order', '<table align=\"left\" border=\"0\" cellpadding=\"4\" cellspacing=\"2\" style=\"border:1px solid #CCC; width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"text-align:center\"><span style=\"font-size:18px\"><strong>ORDER DETAIL #[orderid]</strong></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:top\"><strong>Personal Detail :</strong><br />\r\n			[tag_name]<br />\r\n			[tag_email]<br />\r\n			[tag_mobile]</td>\r\n			<td style=\"text-align:right; vertical-align:top\"><strong>Shipping Detail:</strong><br />\r\n			[tag_ship_address]<br />\r\n			&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\">[tag_list_order]</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: right; width: 80%;\">Total Purchase : [tag_currency]</td>\r\n			<td style=\"text-align: right; width: 20px;\">[tag_total_purchase]</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:right\">Voucher Discount (-) : [tag_currency]</td>\r\n			<td style=\"text-align:right\">[tag_vch]</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:right\">Shipping (+) : [tag_currency]</td>\r\n			<td style=\"text-align:right\">[tag_shipping]</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align:right\"><strong>Grand Total : </strong>[tag_currency]</td>\r\n			<td style=\"text-align:right\"><strong>[tag_grand_total]</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Thank&#39;s For Order</strong></p>\r\n\r\n<p><strong>Armaspart</strong></p>\r\n', 'EMAIL_ORDER_CONFIRMATION', 'id', 'N', ''),
(7, 'Filantropi Indonesia', 'no-reply@filantropi.or.id', '', 'humas@filantropi.or.id', 'admin@filantropi.or.id', 'Approved Member Philanthropy Indonesia', '<p style=\"text-align:center\"><strong>[tag_logo]</strong></p>\n\n<p><span style=\"font-size:20px\">Approved Member Philanthropy Indonesia</span></p>\n\n<p>Pengajuan keanggotaan filantropi indonesia anda sudah di terima. Untuk selanjutnya silahkan untuk membayar iuran agar keanggotaan anda dapat di aktivasi.</p>\n\n<p>&nbsp;</p>\n\n<p>Thanks</p>\n\n<p>Administrator</p>\n', 'EMAIL_APPROVED_MEMBER', 'en', 'Y', 'Widu394IWur83ik'),
(8, 'Filantropi Indonesia', 'no-reply@filantropi.or.id', '', 'humas@filantropi.or.id', 'admin@filantropi.or.id', 'Persetujuan Anggota Filantropi Indonesia', '<p style=\"text-align:center\"><strong>[tag_logo]</strong></p>\n\n<p><span style=\"font-size:20px\">Persetujuan Keanggotaan Filantropi Indonesia</span></p>\n\n<p>Pengajuan keanggotaan filantropi indonesia anda sudah di terima. Untuk selanjutnya silahkan untuk membayar iuran agar keanggotaan anda dapat di aktivasi.</p>\n\n<p>&nbsp;</p>\n\n<p>Terima Kasih</p>\n\n<p>Sekretariat<br />\nFilantropi Indonesia<br />\n<a href=\"mailto:?info@filantropi.or.id?subject=Persetujuan%20Keanggotaan%20Filantropi%20Indonesia\">info@filantropi.or.id</a></p>\n', 'EMAIL_APPROVED_MEMBER', 'id', 'Y', 'Widu394IWur83ik'),
(9, 'Filantropi Indonesia', 'no-reply@filantropi.or.id', '', 'humas@filantropi.or.id', 'admin@filantropi.or.id', 'Membership activation of Philanthropy Indonesia', '<p style=\"text-align:center\"><strong>[tag_logo]</strong></p>\n\n<p style=\"text-align:center\"><span style=\"font-size:20px\">Membership activation of Philanthropy Indonesia</span></p>\n\n<p>Keanggotaan anda sudah di aktivasi dengan detail seperti berikut :</p>\n\n<p>Username : <strong>[tag_username]</strong></p>\n\n<p>Password :&nbsp;<strong>[tag_password]</strong></p>\n\n<p>&nbsp;</p>\n\n<p>Terima Kasih</p>\n\n<p>Administrator</p>\n', 'EMAIL_ACTIVED_MEMBER', 'en', 'Y', 'Lwi8duehYdu8wiK'),
(10, 'Filantropi Indonesia', 'no-reply@filantropi.or.id', '', 'humas@filantropi.or.id', 'admin@filantropi.or.id', 'Aktivasi Keanggotaan Filantropi Indonesia', '<p style=\"text-align:center\"><strong>[tag_logo]</strong></p>\n\n<p style=\"text-align:center\"><span style=\"font-size:20px\">Aktivasi Keanggotaan Filantropi Indonesia</span></p>\n\n<p>Keanggotaan anda sudah di aktivasi dengan detail seperti berikut :</p>\n\n<p>Username : <strong>[tag_username]</strong></p>\n\n<p>Password :&nbsp;<strong>[tag_password]</strong></p>\n\n<p>&nbsp;</p>\n\n<p>Terima Kasih</p>\n\n<p>Sekretariat<br />\nFilantropi Indonesia<br />\n<a href=\"mailto:?info@filantropi.or.id?subject=Aktivasi%20Keanggotaan%20Filantropi%20Indonesia\">?info@filantropi.or.id</a></p>\n', 'EMAIL_ACTIVED_MEMBER', 'id', 'Y', 'Lwi8duehYdu8wiK'),
(11, 'Filantropi Indonesia', 'no-reply@filantropi.or.id', 'admin@filantropi-indonesia.org', 'humas@filantropi.or.id', 'admin@filantropi.or.id', 'Formulir Kontak', '<p><strong>Data Pesan masuk</strong></p>\n\n<p><strong>Nama :</strong> [tag_name]</p>\n\n<p><strong>Email :</strong> [tag_email]</p>\n\n<p><strong>No. HP : </strong>[tag_mobile]</p>\n\n<p><strong>Pesan :</strong></p>\n\n<p>[tag_email_content]</p>\n\n<p>&nbsp;</p>\n\n<p><strong>Terima Kasih</strong></p>\n\n<p>Sekretariat<br />\nFilantropi Indonesia<br />\n<a href=\"mailto:info@filantropi.or.id\">info@filantropi.or.id?</a></p>\n', 'EMAIL_FORM_CONTACT', 'id', 'Y', 'Kw380IWj3jO28d0'),
(12, 'Filantropi Indonesia', 'no-reply@filantropi.or.id', 'admin@filantropi-indonesia.org', 'humas@filantropi.or.id', 'admin@filantropi.or.id', 'Contact Form', '<p><strong>Data Pesan masuk</strong></p>\n\n<p><strong>Nama :</strong> [tag_name]</p>\n\n<p><strong>Email :</strong> [tag_email]</p>\n\n<p><strong>No. HP : </strong>[tag_mobile]</p>\n\n<p><strong>Pesan :</strong></p>\n\n<p>[tag_email_content]</p>\n\n<p>&nbsp;</p>\n\n<p><strong>Terima Kasih</strong></p>\n\n<p><em>Administrator</em></p>\n\n<p><em>Filantropi Indonesia</em></p>\n', 'EMAIL_FORM_CONTACT', 'en', 'Y', 'Kw380IWj3jO28d0');
