(9, 'Welcome Homepage Title', 'welcome_homepage_title', 'Welcome to Filantropi Indonesia', 'en', 'Y', 'IFTpTNqNCNW8qFI'),
(10, 'Welcome Homepage Text', 'welcome_homepage_text', 'Filantropi Indonesia merupakan perhimpunan independen dan nirlaba beranggotakan organisasi dan jaringan filantropi yang berupaya memajukan kedermawan sosial untuk mewujudkan keadilan dan pembangunan', 'id', 'Y', 'D76EtaoUJl8bE9f'),
(11, 'Welcome Homepage Text', 'welcome_homepage_text', 'Filantropi Indonesia is an independent, non-profit association of philanthropy organizations and network seeking to promote ethical social generosity to achieve social justice and sustainable developm', 'en', 'Y', 'D76EtaoUJl8bE9f'),
(12, 'Join Our Membership Text', 'join_our_membership_text', 'Filantropi Indonesia membuka keanggotaan bagi lembaga filantropi berbadan hukum untuk meningkatkan kapasitas dan memperluas jaringan', 'id', 'Y', 'N7VGzjzqF7wW8AE'),
