INSERT INTO `cd_member` (`member_uid`, `member_name`, `member_username`, `member_password`, `member_directur_name`, `member_contact_name`, `member_contact_position`, `member_contact_email`, `member_address`, `member_telephone`, `member_type`, `member_due`, `member_registerdate`, `member_status`, `member_activation_code`, `member_lastlogin`, `member_logo`, `member_signature`, `member_profile_id`, `member_profile_en`, `member_slug`, `member_super`) VALUES
(1, 'Mien R. Uno Foundation', 'MieRUnoFou685', 'SXCTwYb0712/odHRddf+qe+KPuHrWRRNQ1xJUbL1yJIx4I0IqRlXUwutTOHXGKMHkxTaYyWVXoNBOQFgIc6HEQ==', 'Suryani Indahsari', 'Suryani Indahsari', 'Direktur Eksekutif', 'suryani.indahsari@mruf.org', 'Jalan Jenggala II No. 9, Kebayoran Baru, Jakarta 12110', '0217234238', 'Q4lwWcOR5HlV1JE', 'FsJA03NT2CcZDWN', '2016-10-24 13:04:24', 'Active', '', '0000-00-00 00:00:00', '6381d8ff9e0df42f8640dfa73bbd947b.png', '', '', '', 'mien-r-uno-foundation\r\n\r\n', 'N'),
(2, 'Rumah Zakat', 'RumZak502', 'cxSWQbWRPjA0X6oVP0Jbpi75cqBq9KQZTlUtuJGk+DwsM8ogOyCeZ++H7INpk8O9T74770KzNl9CEo0M1E0zFg==', 'Nur Effendi', 'Muhammad Branata', 'Head of Overseas Department', 'branata@rumahzakat.org', 'Jalan Turangga No 25 C Bandung 40263', '0227332407 ', 'Q4lwWcOR5HlV1JE', 'FsJA03NT2CcZDWN', '2016-10-24 13:08:30', 'Active', '', '0000-00-00 00:00:00', '696b515dccdf59dbd631cc8087279fcc.jpg', '', '<p>RZ (Rumah Zakat) adalah lembaga filantropi yang mengelola zakat, infak, sedekah, serta dana sosial lainnya melalui program-program pemberdayaan masyarakat.</p>\n\n<p><strong>Jenis Organisasi</strong>:&nbsp;Implementing/Pelaksana program (menggalang dana/hibah dari pihak lain untuk pendanaan program pemberdayaan ke masyarakat)</p>\n\n<p><strong>Bidang/Area Program</strong>:&nbsp;Program pemberdayaan Rumah Zakat direalisasikan melalui empat rumpun utama yaitu Senyum Juara (pendidikan), Senyum Sehat (kesehatan), Senyum Mandiri (pemberdayaan ekonomi), serta Senyum Lestari (inisiatif kelestarian lingkungan).</p>\n\n<p><strong>Program Utama</strong>:</p>\n\n<ul>\n	<li>Senyum Juara (Program Pemberdayaan RZ di bidang Pendidikan)</li>\n	<li>Senyum Sehat (Program Pemberdayaan RZ di bidang Kesehatan)</li>\n	<li>Senyum Mandiri (Program Pemberdayaan RZ di bidang Ekonomi)</li>\n	<li>Senyum Lestari (Program Pemberdayaan RZ di bidang inisiatif kelestarian Lingkungan)</li>\n</ul>\n\n<p><strong>Cakupan Program</strong>: Internasional dan Nasional</p>\n\n<p><strong>Lokasi Program</strong>:</p>\n\n<ul>\n	<li>Aceh</li>\n	<li>Banten</li>\n	<li>Jakarta</li>\n	<li>Kalimantan Selatan</li>\n	<li>Kalimantan Timur</li>\n	<li>Kepulauan Riau</li>\n	<li>Lampung</li>\n	<li>Papua</li>\n	<li>Riau</li>\n	<li>Sulawesi Selatan</li>\n	<li>Sumatera Barat</li>\n	<li>Sumatera Selatan</li>\n	<li>Sumatera Utara</li>\n	<li>Yogyakarta</li>\n	<li>Kalimantan Barat</li>\n	<li>Jawa Timur</li>\n	<li>Jawa Tengah</li>\n	<li>Jawa Barat</li>\n</ul>\n\n<p><strong>Cakupan Tujuan Pembangunan Berkelanjutan (SDGs)</strong>:</p>\n\n<ul>\n	<li>Tanpa Kemiskinan</li>\n	<li>Tanpa Kelaparan</li>\n	<li>Kehidupan Sehat dan Sejahtera</li>\n	<li>Pendidikan Berkualitas</li>\n	<li>Air Bersih dan Sanitasi Layak</li>\n	<li>Pekerjaan Layak dan Pertumbuhan Ekonomi</li>\n	<li>Berkurangnya Kesenjangan</li>\n	<li>Kemitraan untuk Mencapai Tujuan</li>\n</ul>\n\n<p>&nbsp;</p>\n', '<p>RZ (Zakat House) is a philanthropic institution that manages zakat, infaq, donation, and other social funds through community empowerment&nbsp;programs.</p>\n\n<p><strong>Organization Type</strong>:&nbsp;Implementing (raise&nbsp;funds / grants from other parties to&nbsp;funding its own programs)</p>\n\n<p><strong>Program Area</strong>: Rumah Zakat&#39;s empowerment programs are implemented&nbsp;through four main principles, they are Champion Smile (education), Healthy Smile (health), Independent Smile (economic empowerment), and Sustainable Smile (environmental sustainability initiative).</p>\n\n<p><strong>Main Programs</strong>:</p>\n\n<ul>\n	<li>Champion Smile&nbsp;</li>\n	<li>Healthy Smile&nbsp;</li>\n	<li>Independent Smile&nbsp;</li>\n	<li>Sustainable Smile</li>\n</ul>\n\n<p><strong>Program Scope</strong>: International and National</p>\n\n<p><strong>Program Locations</strong>:</p>\n\n<ul>\n	<li>Aceh</li>\n	<li>Banten</li>\n	<li>Jakarta</li>\n	<li>Kalimantan Selatan</li>\n	<li>Kalimantan Timur</li>\n	<li>Kepulauan Riau</li>\n	<li>Lampung</li>\n	<li>Papua</li>\n	<li>Riau</li>\n	<li>Sulawesi Selatan</li>\n	<li>Sumatera Barat</li>\n	<li>Sumatera Selatan</li>\n	<li>Sumatera Utara</li>\n	<li>Yogyakarta</li>\n	<li>Kalimantan Barat</li>\n	<li>Jawa Timur</li>\n	<li>Jawa Tengah</li>\n	<li>Jawa Barat</li>\n</ul>\n\n<p><strong>Sustainable Development Goals&nbsp;(SDGs) covered</strong>:</p>\n\n<p>#1 No Poverty</p>\n\n<p>#2 Zero Hunger</p>\n\n<p>#3 Good Health and Well-Being</p>\n\n<p>#4 Quality Education</p>\n\n<p>#6 Clean Water and Sanitation</p>\n\n<p>#8 Decent Work and Economic Growth</p>\n\n<p>#10 Reduced Inequalities</p>\n\n<p>#17 Partnerships for the Goals</p>\n', 'rumah-zakat', 'N'),
(3, 'Yayasan Adaro Bangun Negeri', 'YayAdaBanNeg990', 'Gzwm6ZXJ1OLSow0KCfAImHQ0isO8WY2dSzxTTlo++jxs6b6WoeZ9T8yT7iUe1ZoFxxp9HqUQWTf+k0rvJF9psg==', 'Okty Damayanti', 'Fitri Hapsari Pramudita', 'Asisten', 'fitri.pramudita@adaro.com', 'Menara Karya, 18th Floor, Jl. H.R. Rasuna Said Block X-5, Kav. 1-2. Jakarta 12950', '02125533000', 'Q4lwWcOR5HlV1JE', 'FsJA03NT2CcZDWN', '2016-10-24 13:10:55', 'Active', '', '0000-00-00 00:00:00', '9dd8db840a4a488391b5a4af49c729c8.jpg', '', '<p><a href=\"http://www.adaro.com/communities/social-responsibility/\" target=\"_blank\">Yayasan Adaro Bangun Negeri</a>&nbsp;adalah yayasan korporasi yang dibentuk oleh ADARO Group untuk mendukung pencapaian misi perusahaan yaitu mendukung peningkatan kesejahteraan masyarakat dan pembangunan nasional. YABN selalu berupaya menjadi katalist dalam masyarakat yang memberikan dampak perubahaan kearah lebih baik dalam bidang ekonomi lokal, pendidikan, kesehatan, sosial budaya dan lingkungan yang lebih lestari. Pendekatan inklusif selalu menjadi pedoman dalam menjalankan program disertai peningkatan kualitas sumber daya manusia yang mengelola, menggerakkan dan membesarkan kiprah YABN.</p>\n\n<p><strong>Jenis Organisasi</strong>:&nbsp;Implementing/Pelaksana program (menggalang dana/hibah dari pihak lain untuk pendanaan program organisasi)</p>\n\n<p><strong>Bidang/Area Program</strong>:&nbsp;Peningkatan Ekonomi Lokal, Pendidikan, Kesehatan, Lingkungan, Sosial Budaya</p>\n\n<p><strong>Program Utama:</strong></p>\n\n<ul>\n	<li>Air Bersih dan Sanitasi yang Layak</li>\n	<li>Peningkatan kualitas pendidikan melalui PAUD dan Community-based Scientific Education Program</li>\n</ul>\n\n<p><strong>Cakupan Program:</strong>&nbsp;Nasional</p>\n\n<p><strong>Lokasi Program</strong>:&nbsp;Kalimantan Selatan</p>\n\n<p><strong>Cakupan Tujuan Pembangunan Berkelanjutan (SDGs):</strong></p>\n\n<ul>\n	<li>Kehidupan Sehat dan Sejahtera (#3)</li>\n	<li>Pendidikan Berkualitas (#4)</li>\n	<li>Air Bersih dan Sanitasi Layak (#6)</li>\n	<li>Kemitraan untuk Mencapai Tujuan (#17)</li>\n</ul>\n\n<p>&nbsp;</p>\n', '<p><a href=\"http://www.adaro.com/communities/social-responsibility/\">Adaro Bangun Negeri Foundation</a> is a corporate foundation established by ADARO Group to support the achievement of the company&#39;s mission to support improvement of social welfare and national development. YABN always strives to be a catalyst in the communities and&nbsp;provides the change towards a better impact in the field of local economy, education, health, social, cultural and environmental sustainability. Inclusive approach has always been a guide in running the program while&nbsp;improving the quality of human resources who&nbsp;manage, mobilize and increase the reputation of YABN.</p>\n\n<p><strong>Organization Type</strong>:&nbsp;Implementing (raise&nbsp;funds / grants from other parties to fund its own&nbsp;programs)</p>\n\n<p><strong>Program Areas</strong>: Local Economy Empowerment, Education, Health, Environmental, Social Culture</p>\n\n<p><strong>Main Programs:</strong></p>\n\n<ul>\n	<li>Clean Water and Sanitation</li>\n	<li>Education quality improvement through PAUD (early childhood education) and&nbsp;Community-based Scientific Education Program</li>\n</ul>\n\n<p><strong>Program Scope:</strong>&nbsp;National</p>\n\n<p><strong>Program Location</strong>:&nbsp;Kalimantan Selatan</p>\n\n<p><strong>Sustainable Development Goals&nbsp;(SDGs) covered:</strong></p>\n\n<ul>\n	<li>Good Health and Well-Being&nbsp;(#3)</li>\n	<li>Quality Education&nbsp;(#4)</li>\n	<li>Clean Water and Sanitation&nbsp;(#6)</li>\n</ul>\n\n<p>Partnerships for the Goals&nbsp;(#17)</p>\n', 'yayasan-adaro-bangun-negeri', 'N'),
(4, 'Yayasan Indocement', 'YayInd965', '3EcWPrqnFflVTVspW/tDu6SiTmjLVyi0LXTD3WPElKcpw96CPYFFxseVuzAJ0F2hMCWpnD+HsMOUrWv9aOUTvg==', 'Kuky Permana', 'Ibu Mita', 'Asisten pak Franky', 'psd12345@gmail.com', 'Jl. Raya Puspanegara No. 1 Citeureup Kab. Bogor', '0811916190', 'Q4lwWcOR5HlV1JE', 'FsJA03NT2CcZDWN', '2016-10-24 13:11:53', 'Active', '', '0000-00-00 00:00:00', '0d374220f131da3c2f1ec5bd5f4d3370.jpg', '', '', '', 'yayasan-indocement', 'N'),
(5, 'Yayasan Unilever Indonesia', 'YayUniInd767', 'tyDA0DtJz22t6/kSGnEIuEAb/4l/A3ObVeftkAV1nvB04GVAR6hPsCmjbvlIEgOBHSNQhe1+9I12guGL1ckiiQ==', 'Sinta Kaniawati', 'Sinta Kaniawati', 'General Manager', 'sinta.kaniawati@unilever.com', 'Graha Unilever Lt. 12, Jl. Jend. Sudirman Kav. 15, Jakarta 12930', '0215262112', 'Q4lwWcOR5HlV1JE', 'FsJA03NT2CcZDWN', '2016-10-24 13:12:33', 'Active', '', '0000-00-00 00:00:00', '34e9d3ff7b632a6f29ee6318d46f48bf.png', '', '<p><a href=\"https://www.unilever.co.id/about/who-we-are/yayasan-unilever-indonesia/tentang-yayasan-unilever-indonesia/\">Yayasan Unilever Indonesia</a>&nbsp;adalah sarana utama dalam mengimplementasikan Unilever Sustainable Living Plan di Indonesia.&nbsp;Tonggak sejarah dimulai pada 27 November 2000. Misi dari Yayasan Unilever Indonesia adalah untuk mencari dan memberdayakan potensi masyarakat, memberikan nilai tambah bagi masyarakat, menyatukan kekuatan dengan mitra-mitranya dan bertindak sebagai katalis untuk pembentukan kemitraan.&nbsp;Keberadaan yayasan membuat kegiatan CSR Unilever lebih dari sekadar kegiatan amal yang temporer. Program yang berkelanjutan menjadi fokus utama; hal yang tidak kalah penting adalah Unilever tumbuh dan berkembang bersama dengan para mitra kerja yang terjun ke lapangan.</p>\r\n\r\n<p><strong>Jenis Organisasi</strong>: Grant maker/Pemberi Hibah&nbsp;(memiliki sumber dana sendiri dan menyalurkannya ke organisasi mitra atau langsung ke masyarakat)</p>\r\n\r\n<p><strong>Bidang/Area Program</strong>:</p>\r\n\r\n<ul>\r\n	<li>Kesehatan dan Nutrisi</li>\r\n	<li>Lingkungan</li>\r\n	<li>Peningkatan Penghidupan</li>\r\n</ul>\r\n\r\n<p><strong>Cakupan Program</strong>: Nasional</p>\r\n\r\n<p><strong>Lokasi Program (Provinsi)</strong>:</p>\r\n\r\n<ul>\r\n	<li>Sumatera Utara</li>\r\n	<li>DKI Jakarta</li>\r\n	<li>Jawa Barat</li>\r\n	<li>Daerah Istimewa Yogyakarta</li>\r\n	<li>Jawa Tengah</li>\r\n	<li>Jawa Timur</li>\r\n	<li>Bali</li>\r\n	<li>Nusa Tenggara Timur</li>\r\n	<li>Kalimantan Timur</li>\r\n	<li>Kalimantan Selatan</li>\r\n	<li>Sulawesi Selatan</li>\r\n	<li>Sulawesi Utara</li>\r\n	<li>Papua Barat</li>\r\n</ul>\r\n\r\n<p><strong>Mitra dalam Penyaluran Hibah</strong>: NGO/LSM, Perguruan Tinggi, Pemerintah, Lembaga Riset</p>\r\n\r\n<p><strong>Cakupan Tujuan Pembangunan Berkelanjutan (SDGs)</strong>:</p>\r\n\r\n<p>#1 Tanpa Kemiskinan</p>\r\n\r\n<p>#2 Tanpa Kelaparan</p>\r\n\r\n<p>#3 Kehidupan Sehat dan Sejahtera</p>\r\n\r\n<p>#6 Air Bersih dan Sanitasi Layak</p>\r\n\r\n<p>#8 Pekerjaan Layak dan Pertumbuhan Ekonomi</p>\r\n\r\n<p>#11 Kota dan Pemukiman yang Berkelanjutan</p>\r\n\r\n<p>#12 Konsumsi dan Produksi yang Bertanggungjawab</p>\r\n\r\n<p>#13 Penanganan Perubahan Iklim</p>\r\n\r\n<p>#15 Ekosistem Daratan</p>\r\n\r\n<p>#17 Kemitraan untuk Mencapai Tujuan</p>\r\n', '<p>Unilever Indonesia Foundation is the means of implementing the Unilever Sustainable Living Plan in Indonesia. The milestones began on 27 November 2000. The mission of Unilever Indonesia Foundation is to seek and empowering potential of the community, providing added value to the community, joining forces with its partners and act as a catalyst for the formation of the partnership. The existence of the foundation make Unilever&#39;s CSR activities more than just temporary charities. Building a&nbsp;sustainable&nbsp;program became the main focus; another important thing is Unilever can&nbsp;grow and develop together with our partners in the field.</p>\r\n\r\n<p><strong>Organization type</strong>: Grant maker (have their own funding sources and distribute it to partner organizations or directly to the public)</p>\r\n\r\n<p><strong>Program Areas</strong>:</p>\r\n\r\n<ul>\r\n	<li>Health and Nutrition</li>\r\n	<li>Environment</li>\r\n	<li>Life Improvement</li>\r\n</ul>\r\n\r\n<p><strong>Program Scale</strong>: National</p>\r\n\r\n<p><strong>Program Locations (Provinces)</strong>:</p>\r\n\r\n<ul>\r\n	<li>Sumatera Utara</li>\r\n	<li>DKI Jakarta</li>\r\n	<li>Jawa Barat</li>\r\n	<li>Daerah Istimewa Yogyakarta</li>\r\n	<li>Jawa Tengah</li>\r\n	<li>Jawa Timur</li>\r\n	<li>Bali</li>\r\n	<li>Nusa Tenggara Timur</li>\r\n	<li>Kalimantan Timur</li>\r\n	<li>Kalimantan Selatan</li>\r\n	<li>Sulawesi Selatan</li>\r\n	<li>Sulawesi Utara</li>\r\n	<li>Papua Barat</li>\r\n</ul>\r\n\r\n<p><strong>Partners</strong>: NGO/CSO, higher education institutions, government, research institute</p>\r\n\r\n<p><strong>Sustainable Development Goals&nbsp;(SDGs) covered</strong>:</p>\r\n\r\n<p>#1&nbsp;No Poverty</p>\r\n\r\n<p>#2&nbsp;Zero Hunger</p>\r\n\r\n<p>#3&nbsp;Good Health and Well-Being</p>\r\n\r\n<p>#6&nbsp;Clean Water and Sanitation</p>\r\n\r\n<p>#8&nbsp;Decent Work and Economic Growth</p>\r\n\r\n<p>#11&nbsp;Sustainable Cities and Communities</p>\r\n\r\n<p>#12&nbsp;Responsible Consumption and Production</p>\r\n\r\n<p>#13&nbsp;Climate Action</p>\r\n\r\n<p>#15&nbsp;Life on Land</p>\r\n\r\n<p>#17 Partnerships for the Goals</p>\r\n', 'yayasan-unilever-indonesia', 'N'),
(6, 'The Nature Conservancy (Yayasan Koservansi Alam Nusantara)', 'TheNatCon487', 'dxbZqIPGbeC4AoA/urRA3LK0lNdlLElIhBlRd8XPPNcU8E3zo6WxavhaFj30MLx3Ta9uM+l6uk1kzKqvz+4G+A==', 'Rizal Algamar', 'Chintya Dian Astuti', 'Corporate Engagement Manager', 'cintya.astuti@tnc.org', 'Graha Iskandarsyah Lt. 3, Jl.  Iskandarsyah Raya No. 66C, Jakarta Selatan', '02172792043', 'Q4lwWcOR5HlV1JE', 'FsJA03NT2CcZDWN', '2016-10-24 13:13:13', 'Active', '', '0000-00-00 00:00:00', 'b10df0c3fc4575823d1ffea57975f47a.png', '', '', '', 'the-nature-conservancy-yayasan-koservansi-alam-nus', 'N'),
(7, 'Yayasan Bakti Kita', 'YayBakKit793', 'QlN5dcIgQUI2HrfIuNbgSE04p0Z3gLI8Z39dmX0vzNYg8HYI4JjLlRTT0e4disoJJw+aISfe8luh1i1n8FXS+w==', 'Franciscus  Welirang', 'Rina Astuti', 'Operations Manager', 'rinaastuti@gmail.com', 'Jl. Kyai Maja no. 11, Kebayoran Baru, Jakarta Selatan 12130', '021 72789583/84', 'Q4lwWcOR5HlV1JE', 'FsJA03NT2CcZDWN', '2016-10-24 13:14:05', 'Active', '', '0000-00-00 00:00:00', 'e629412c597d4534bf14c2e69dafe4b9.png', '', '<p><strong>PROFIL YAYASAN BAKTI KITA</strong></p>\n\n<p style=\"text-align:justify\">Yayasan Bakti kita di dirikan di Jakarta dengan Akta Notaris No. 35 tanggal 7 Maret 2013 Popie Savitri Martosuhardjo Pharmanto, S.H , &nbsp;dan di Sahkan di Kemenkumham No. AHU-7780.AH.01.04 tahun 2013, dengan Alamat Jl. Kyai Maja No. 11, Kebayoran Baru, Jakarta - Selatan.&nbsp;Adapun Ketua Yayasan Bakti Kita adalah Bapak FRANCISCUS WELIRANG.</p>\n\n<p style=\"text-align:justify\">Yayasan Bakti Kita ini didirikan untuk tujuan sosial , Pemberdayaan Usaha Kecil/Mikro , dalam mendapatkan dana ataupun pelatihan, yang tertuang dalam kegiatan Yayasan Bakti Kita.</p>\n\n<p>Kegiatan Yayasan Bakti Kita &nbsp;meliputi :</p>\n\n<ol>\n	<li>\n	<p>Melakukan Pembinaan dan Pemberdayaan bagi para Pengusaha kecil/Mikro agar dapat lebih mengembangkan dan memajukan usahanya.</p>\n	</li>\n	<li>\n	<p>Menyelenggarakan Pendidikan, Pelatihan dan kajian yang diperlukan bagi pengusaha kecil/mikro agar dapat lebih meningkatkan ketrampilan dan kemampuannya.</p>\n	</li>\n	<li>\n	<p>Menerbitkan bulletin yang diperlukan untuk mengembangkan usaha kecil/mikro yang tidak diperjualbelikan.&nbsp;</p>\n	</li>\n</ol>\n\n<p>&nbsp;</p>\n', '', 'yayasan-bakti-kita', 'N'),
(8, 'JAPFA Foundation', 'JAPFou784', 'xeNRAYfWZ6B3523VMREWfEJVPy+ZL+1gm6ThUGkCdMxifsi0m96PnzRvI9aKA7rJQvuCFDaJYJ5Qud/igbIpKQ==', 'Andi Prasetyo', 'Andi Prasetyo', 'Ketua Yayasan', 'andi.prasetyo@japfafoundation.org', 'Wisma Millenia 7th floor, Jl. MT Haryono Kav. 16 Jakarta Selatan 12810', '02128545680', 'Q4lwWcOR5HlV1JE', 'FsJA03NT2CcZDWN', '2016-10-24 13:14:50', 'Active', '', '0000-00-00 00:00:00', 'bb7ecef235b7869e9eeb7874d69ce77d.jpg', '', '<p><a href=\"http://www.japfafoundation.org/\" target=\"_blank\">JAPFA Foundation</a>&nbsp;didirikan oleh PT Japfa Comfeed Indonesia Tbk pada bulan Juli 2015 di Jakarta. Japfa Foundation merupakan Corporate Foundation yang bertindak sebagai penyelenggara dari seluruh kegiatan sosial perusahaan di seluruh area PT Japfa Comfeed Indonesia Tbk beroperasi terutama di Indonesia. Visi utamanya adalah memaksimalkan potensi kaum muda melalui pendidikan, gizi dan olahraga.</p>\r\n\r\n<p><strong>Jenis organisasi</strong>:&nbsp;Intermediary/Perantara (menggalang dana/hibah dari pihak lain dan menyalurkan ke lembaga yang lebih kecil)</p>\r\n\r\n<p><strong>Bidang/Area Program</strong>:&nbsp;pendidikan, gizi dan olahraga</p>\r\n\r\n<p><strong>Program Utama</strong>:</p>\r\n\r\n<ul>\r\n	<li>community open space initiatives</li>\r\n	<li>financial aid and scholarships</li>\r\n	<li>school development programs</li>\r\n	<li>nutrition improvement programs</li>\r\n</ul>\r\n\r\n<p><strong>Cakupan Program</strong>: Internasional</p>\r\n\r\n<p><strong>Lokasi program</strong>:</p>\r\n\r\n<ul>\r\n	<li>Sulawesi Selatan</li>\r\n	<li>Sumatera Barat</li>\r\n	<li>Sumatera Utara</li>\r\n	<li>Yogyakarta</li>\r\n	<li>Kalimantan Selatan</li>\r\n	<li>Jawa Timur</li>\r\n	<li>Jawa Tengah</li>\r\n	<li>Jawa Barat</li>\r\n	<li>Jakarta</li>\r\n	<li>Aceh</li>\r\n</ul>\r\n\r\n<p><strong>Cakupan Tujuan Pembangunan Berkelanjutan (SDGs)</strong>:</p>\r\n\r\n<ul>\r\n	<li>Kemitraan untuk Mencapai Tujuan (#17)</li>\r\n	<li>Berkurangnya Kesenjangan (#10)</li>\r\n	<li>Industri, Inovasi, dan Infrastruktur (#9)</li>\r\n	<li>Pendidikan Berkualitas (#4)</li>\r\n	<li>Kehidupan Sehat dan Sejahtera (#3)</li>\r\n</ul>\r\n', '<p><a href=\"http://www.japfafoundation.org/\" target=\"_blank\">JAPFA Foundation</a>&nbsp;was established by PT Japfa Comfeed Indonesia Tbk on July 2015 in Jakarta. Japfa Foundation is acting as the organizer of the entire social activity in all areas of PT Japfa Comfeed Indonesia Tbk which operates mainly in Indonesia. Its main vision is to maximize the potential of young people through education, nutrition and exercise.</p>\r\n\r\n<p><strong>Organization Type</strong>:&nbsp;Intermediary (raise&nbsp;funds / grants from other parties and distribute to smaller institutions)</p>\r\n\r\n<p><strong>Program&nbsp;Areas</strong>: Education, Nutrition and Sports</p>\r\n\r\n<p><strong>Main Programs</strong>:</p>\r\n\r\n<ul>\r\n	<li>community open space initiatives</li>\r\n	<li>financial aid and scholarships</li>\r\n	<li>school development programs</li>\r\n	<li>nutrition improvement programs</li>\r\n</ul>\r\n\r\n<p><strong>Program Scope</strong>: International</p>\r\n\r\n<p><strong>Program Locations</strong>:</p>\r\n\r\n<ul>\r\n	<li>Sulawesi Selatan</li>\r\n	<li>Sumatera Barat</li>\r\n	<li>Sumatera Utara</li>\r\n	<li>Yogyakarta</li>\r\n	<li>Kalimantan Selatan</li>\r\n	<li>Jawa Timur</li>\r\n	<li>Jawa Tengah</li>\r\n	<li>Jawa Barat</li>\r\n	<li>Jakarta</li>\r\n	<li>Aceh</li>\r\n</ul>\r\n\r\n<p><strong>Sustainable Development Goals&nbsp;(SDG) covered</strong>:</p>\r\n\r\n<ul>\r\n	<li>Good Health and Well-Being (#3)</li>\r\n	<li>Quality Education&nbsp;(#4)</li>\r\n	<li>Industry, Innovation and Infrastructure&nbsp;(#9)</li>\r\n	<li>Reduced Inequalities&nbsp;(#10)</li>\r\n</ul>\r\n\r\n<p>Partnerships for the Goals&nbsp;(#17)</p>\r\n', 'japfa-foundation', 'N'),
(9, 'Eka Tjipta Foundation (Yay. Dharma Eka Tjipta Widj', 'EkaTjiFou804', '/CXEG3Y44hikIPqkbw540IdE5itDWqhm/ux+yNl92+mLgLbcCnAdJKe3Oqp9HgAYpYYRRIwIo5tWLqpHAeVN9Q==', 'Joice Budisusanto', 'Gita Debora', '-', 'gitadebora@ekatjipta.org', 'Plaza Sinar Mas 2 Lantai 3 Jl. MH Thamrin Kav. 51, Jakarta Pusat 10350', '0811 9893 925', 'Q4lwWcOR5HlV1JE', 'FsJA03NT2CcZDWN', '2016-10-24 13:15:32', 'Active', '', '0000-00-00 00:00:00', 'c53b82fc780e60dfb42532c23cbd50ec.jpg', '', '', '', 'eka-tjipta-foundation-yay-dharma-eka-tjipta-widjaj', 'N'),
(10, 'Yayasan Tahija', 'YayTah638', 'fnXPjYfBQgfU/dfj7L4zXNPB31SO5Dl5dvfTRAL84/m5j2mHMurVlA70TfXKJsQerS1vcvZg5gcpbjx1BMmLBA==', 'Agus Susanto', 'Silvia Novi', '-', 'silvia.novi@tahijaenterprise.com', 'Gedung Graha Irama Lantai 3 Jl. Rasuna Said Kav. 1-2, Jakarta Selatan 12920', '0215261415', 'Q4lwWcOR5HlV1JE', 'FsJA03NT2CcZDWN', '2016-10-24 13:16:37', 'Active', '', '0000-00-00 00:00:00', 'be2343c4ab506f5f87d43398b5f3f85d.png', '', '<p><strong>Yayasan Tahija</strong></p>\n\n<p>Yayasan Tahija adalah organisasi nirlaba yang didirikan di Jakarta oleh almarhum Ibu Jean Tahija dan Bapak Julius Tahija pada tanggal 21 Maret 1990.&nbsp; Yayasan Tahija merupakan wadah formal prakarsa filantropi keluarga Tahija.&nbsp; Dengan Misi untuk &ldquo;mewujudkan <strong>Indonesia yang lebih baik</strong>, dengan membangun kemitraan untuk prakarsa yang berkesinambungan dalam bidang pendidikan, kebudayaan, kesehatan, pelestarian lingkungan hidup dan pelayanan social&rdquo;, saat ini Yayasan Tahija sedang fokus mendukung kegiatan penelitian Eliminate Dengue&nbsp; Project di kota Yogyakarta, bekerja sama dengan Fakultas Kedokteran Universitas Gadjah Mada dan Monash University, Australia.</p>\n\n<p><strong>Jenis organisasi</strong>:&nbsp; Grant maker/Pemberi Hibah&nbsp;(memiliki sumber dana sendiri dan menyalurkannya ke organisasi mitra atau langsung ke masyarakat)</p>\n\n<p><strong>Bidang/Area Program</strong>:&nbsp;</p>\n\n<ul>\n	<li>Kesehatan</li>\n	<li>Pendidikan</li>\n	<li>Kebudayaan</li>\n	<li>Pelestarian Lingkungan hidup</li>\n	<li>Pelayanan Sosial</li>\n</ul>\n\n<p>&nbsp;</p>\n\n<p><strong>Program Utama</strong>:&nbsp; Eliminate Dengue Project &ndash; Yogyakarta</p>\n\n<p><strong>Cakupan Program</strong>: Nasional</p>\n\n<p><strong>Lokasi Program</strong>:</p>\n\n<ul>\n	<li>Yogyakarta</li>\n	<li>Jawa Tengah</li>\n	<li>Banda Aceh</li>\n</ul>\n\n<p>&nbsp;</p>\n\n<p><strong>Mitra dalam Penyaluran Hibah</strong>: Lembaga Riset, Perguruan Tinggi, NGO/LSM.</p>\n\n<p><strong>Cakupan Tujuan Pembangunan Berkelanjutan (SDGs)</strong>:</p>\n\n<ul>\n	<li>Kehidupan Sehat dan Sejahtera (#3)</li>\n	<li>Pendidikan Berkualitas (#4)</li>\n	<li>Kemitraan untuk Mencapai Tujuan (#17)</li>\n</ul>\n', '<p><strong>Tahija Foundation</strong></p>\n\n<p>Tahija Foundation is a nonprofit organization founded in Jakarta by the late Mrs. Jean Tahija and Mr Julius Tahija on March 21, 1990. The Foundation is a formal institution of Tahija family philanthropic initiatives. With a mission to &quot;build a better Indonesia, by building partnerships for sustainable initiatives in the fields of education, culture, health, environmental protection and social services&quot;; right now, Tahija Foundation is focusing on Eliminate Dengue Project research support in the city of Yogyakarta, working together with the Faculty of Medicine, Gadjah Mada University and Monash University, Australia.</p>\n\n<p><strong>Organization type</strong>:&nbsp; Grant maker (have their own funding sources and distribute it to partner organizations or directly to the public)</p>\n\n<p><strong>Program Areas</strong>:&nbsp;</p>\n\n<ul>\n	<li>Health</li>\n	<li>Education</li>\n	<li>Arts &amp; Culture</li>\n	<li>Environmental Conservation</li>\n	<li>Social services</li>\n</ul>\n\n<p><strong>Main Program</strong>:&nbsp; Eliminate Dengue Project &ndash; Yogyakarta</p>\n\n<p><strong>Program Scale</strong>: National</p>\n\n<p><strong>Program Locations</strong>:</p>\n\n<ul>\n	<li>Yogyakarta</li>\n	<li>Central Java</li>\n	<li>Banda Aceh</li>\n</ul>\n\n<p>&nbsp;<strong>Partners</strong>: Research institutes, University, NGO</p>\n\n<p><strong>Sustainable Development Goals (SDGs) alignment</strong>:</p>\n\n<ul>\n	<li>Good Health and Well-being (#3)</li>\n	<li>Quality Education (#4)</li>\n	<li>Partnerships for the Goals (#17)</li>\n</ul>\n', 'yayasan-tahija', 'N'),
(11, 'Yayasan Danamon Peduli', 'YayDanPed865', 'tspSL5sOVjSzN80g4ygSpt6ZrrbPL5wrSLfDYHtJWZBO5E+HyRXKmnWR/Y1KSz/gcGDZ+0qYNBaJWgXBrRaHRQ==', 'Restu Pratiwi', 'Milawati', 'Administrative Officer', 'milawati@danamonpeduli.or.id', 'Gedung Graha Aktiva, 4th Floor Suite 404, Jl. H.R. Rasuna Said Blok 10-1 Kavling 3 Jak Sel 12950', '02152900291', 'Q4lwWcOR5HlV1JE', 'FsJA03NT2CcZDWN', '2016-10-24 13:17:45', 'Active', '', '0000-00-00 00:00:00', 'b7f4397bc5074ac03f6994214a7c2eac.jpg', '', '<p><a href=\"http://danamonpeduli.org/\" target=\"_blank\">Yayasan Danamon Peduli</a>&nbsp;adalah organisasi yang didirikan oleh Bank Danamon &amp; Adira Finance untuk menjalankan peran Tanggung Jawab Sosial Perusahaan</p>\r\n\r\n<p><strong>Jenis organisasi</strong>:&nbsp;Implementing/Pelaksana program (menggalang dana/hibah dari pihak lain untuk pendanaan program organisasi)</p>\r\n\r\n<p><strong>Bidang/Area Program</strong>:&nbsp;Revitalisasi pasar tradisional, lingkungan, bencana</p>\r\n\r\n<p><strong>Program Utama</strong>:</p>\r\n\r\n<ul>\r\n	<li>Program Pasar Sejahtera</li>\r\n	<li>Program Peduli Lingkungan</li>\r\n	<li>Program Cepat Tanggap Bencana</li>\r\n</ul>\r\n\r\n<p><strong>Cakupan Program</strong>: Nasional</p>\r\n\r\n<p><strong>Lokasi Program</strong>:</p>\r\n\r\n<ul>\r\n	<li>Jawa Tengah</li>\r\n	<li>Jawa Timur</li>\r\n	<li>Kalimantan Timur</li>\r\n	<li>Lampung</li>\r\n	<li>Sulawesi Selatan</li>\r\n	<li>Sumatera Barat</li>\r\n	<li>Yogyakarta</li>\r\n</ul>\r\n\r\n<p><strong>Cakupan Tujuan Pembangunan Berkelanjutan (SDGs)</strong>:</p>\r\n\r\n<ul>\r\n	<li>Tanpa Kemiskinan (#1)</li>\r\n	<li>Tanpa Kelaparan (#2)</li>\r\n	<li>Kehidupan Sehat dan Sejahtera (#3)</li>\r\n	<li>Air Bersih dan Sanitasi Layak (#6)</li>\r\n	<li>Pekerjaan Layak dan Pertumbuhan Ekonomi (#8)</li>\r\n	<li>Berkurangnya Kesenjangan (#10)</li>\r\n	<li>Penanganan Perubahan Iklim (#13)</li>\r\n</ul>\r\n\r\n<p>Kemitraan untuk Mencapai Tujuan (#17)</p>\r\n', '<p><a href=\"http://danamonpeduli.org/\" target=\"_blank\">Yayasan Danamon Peduli</a>&nbsp;is an organization established by Danamon Bank and Adira Finance to implement the company&#39;s Social Responsibilities</p>\r\n\r\n<p><strong>Organization Type</strong>:&nbsp;Implementing (raise&nbsp;funds / grants from other parties to fund its own&nbsp;programs)</p>\r\n\r\n<p><strong>Program Area&nbsp;</strong>: Traditional market revitalization, environment, disaster relief</p>\r\n\r\n<p><strong>Main Programs</strong>:</p>\r\n\r\n<ul>\r\n	<li>Market Welfare Program</li>\r\n	<li>Environmental Care&nbsp;Program</li>\r\n	<li>Disaster Relief Program</li>\r\n</ul>\r\n\r\n<p><strong>Program Scope</strong>:&nbsp;National</p>\r\n\r\n<p><strong>Program Locations</strong>:</p>\r\n\r\n<ul>\r\n	<li>Jawa Tengah</li>\r\n	<li>Jawa Timur</li>\r\n	<li>Kalimantan Timur</li>\r\n	<li>Lampung</li>\r\n	<li>Sulawesi Selatan</li>\r\n	<li>Sumatera Barat</li>\r\n	<li>Yogyakarta</li>\r\n</ul>\r\n\r\n<p><strong>Sustainable Development Goals&nbsp;(SDGs) Covered</strong>:</p>\r\n\r\n<ul>\r\n	<li>No Poverty&nbsp;(#1)</li>\r\n	<li>Zero Hunger&nbsp;(#2)</li>\r\n	<li>Good Health and Well-Being&nbsp;(#3)</li>\r\n	<li>Clean Water and Sanitation&nbsp;(#6)</li>\r\n	<li>Decent Work and Economic Growth&nbsp;(#8)</li>\r\n	<li>Reduced Inequalities&nbsp;(#10)</li>\r\n	<li>Climate Action&nbsp;(#13)</li>\r\n</ul>\r\n\r\n<p>Partnerships for the Goals&nbsp;(#17)</p>\r\n', 'yayasan-danamon-peduli', 'N'),
(12, 'Yayasan Tifa', 'YayTif705', 'vQeTWkaYQl9CwN4JVDrZpCJbTj23TYEaOZAISqyQMW1Ft8uUEE94PC6kh+/3E5chKT0RQJhc4WEuR+okg+5NwQ==', 'Darmawan Triwibowo', 'Darmawan Triwibowo', 'Direktur Eksekutif', 'darmawan@tifafoundation.org', '18 Office Park, 15th Floor C-D Jl. TB. Simatupang No.18 Kebagusan-Pasar Minggu Jaksel. 12520', '02122701427', 'Q4lwWcOR5HlV1JE', 'FsJA03NT2CcZDWN', '2016-10-24 13:18:29', 'Active', '', '0000-00-00 00:00:00', '925d12ce4ecfed9ddfcc9d4d4c7efd50.jpg', '', '<p><a href=\"http://www.tifafoundation.org/\" target=\"_blank\">Yayasan Tifa</a>&nbsp;adalah organisasi pemberi hibah yang bekerja untuk mempromosikan masyarakat terbuka, dengan berperan aktif menguatkan masyarakat sipil di Indonesia. Yayasan yang didirikan pada akhir tahun 2000 ini terus mempertajam fokusnya untuk mengawal proses demokrasi di Indonesia. Setelah mendorong transisi demokrasi dan konsolidasi demokrasi di tahun-tahun sebelumnya, sejak tahun 2012 Tifa berdedikasi memajukan demokrasi yang berkualitas di Indonesia.</p>\r\n\r\n<p><strong>Jenis Organisasi</strong>:&nbsp;Intermediary/Perantara (menggalang dana/hibah dari pihak lain dan menyalurkan ke lembaga yang lebih kecil)</p>\r\n\r\n<p><strong>Bidang/area Program</strong>:&nbsp;Masyarakat Terbuka dan Keberagaman, Keadilan dan Kesetaraan</p>\r\n\r\n<p><strong>Program Utama</strong>:</p>\r\n\r\n<ol>\r\n	<li>Reformasi Keadilan</li>\r\n	<li>Penegakan Hukum</li>\r\n	<li>Praktek Demokrasi</li>\r\n	<li>Tata Kelola Ekonomi</li>\r\n</ol>\r\n\r\n<p><strong>Cakupan Program</strong>: Internasional, Nasional dan Lokal</p>\r\n\r\n<p><strong>Lokasi&nbsp;Program</strong>:</p>\r\n\r\n<ul>\r\n	<li>Aceh</li>\r\n	<li>Bali</li>\r\n	<li>Banten</li>\r\n	<li>Jakarta</li>\r\n	<li>Jambi</li>\r\n	<li>Jawa Barat</li>\r\n	<li>Jawa Tengah</li>\r\n	<li>Jawa Timur</li>\r\n	<li>Kalimantan Barat</li>\r\n	<li>Kalimantan Timur</li>\r\n	<li>Kepulauan Riau</li>\r\n	<li>Lampung</li>\r\n	<li>Maluku</li>\r\n	<li>Maluku Utara</li>\r\n	<li>Nusa Tenggara Barat</li>\r\n	<li>Nusa Tenggara Timur</li>\r\n	<li>Papua</li>\r\n	<li>Papua Barat</li>\r\n	<li>Riau</li>\r\n	<li>Sulawesi Selatan</li>\r\n	<li>Sulawesi Tengah</li>\r\n	<li>Sulawesi Tenggara</li>\r\n	<li>Sulawesi Utara</li>\r\n	<li>Sumatera Barat</li>\r\n	<li>Sumatera Selatan</li>\r\n	<li>Sumatera Utara</li>\r\n	<li>Yogyakarta</li>\r\n</ul>\r\n\r\n<p><strong>Cakupan Tujuan Pembangunan Berkelanjutan (SDGs)</strong>:</p>\r\n\r\n<ul>\r\n	<li>Tanpa Kemiskinan (#1)</li>\r\n	<li>Kehidupan Sehat dan Sejahtera (#3)</li>\r\n	<li>Pendidikan Berkualitas (#4)</li>\r\n	<li>Kesetaraan Gender (#5)</li>\r\n	<li>Air Bersih dan Sanitasi Layak (#6)</li>\r\n	<li>Pekerjaan Layak dan Pertumbuhan Ekonomi (#8)</li>\r\n	<li>Berkurangnya Kesenjangan (#10)</li>\r\n	<li>Kota dan Permukiman yang Berkelanjutan (#11)</li>\r\n	<li>Penanganan Perubahan Iklim (#13)</li>\r\n	<li>Perdamaian, Keadilan, dan Kelembagaan yang Tangguh (#16)</li>\r\n	<li>Kemitraan untuk Mencapai Tujuan (#17)</li>\r\n</ul>\r\n', '<p>Tifa Foundation&nbsp;is a grantmaking organization that works to promote open society, by&nbsp;playing an active role in Indonesia to&nbsp;strengthen the civil society . The Foundation - established in late 2000 - continues to sharpen its focus to oversee the process of democracy in Indonesia. After pushing the democratic transition and consolidation of democracy in previous years, since 2012 Tifa is dedicated in promoting the quality of democracy in Indonesia.</p>\r\n\r\n<p><strong>Organization type</strong>:&nbsp;Intermediary (raise&nbsp;funds / grants from other parties and distribute to smaller institutions)</p>\r\n\r\n<p><strong>Program Area</strong>:&nbsp;Open and diverse, justice and equal community</p>\r\n\r\n<p><strong>Main Programs</strong>:</p>\r\n\r\n<ol>\r\n	<li>Justice Reformation</li>\r\n	<li>Law Enforcement</li>\r\n	<li>Democracy practices</li>\r\n	<li>Economic Governance</li>\r\n</ol>\r\n\r\n<p><strong>Program Scale</strong>: International, National dan Local</p>\r\n\r\n<p><strong>Program Locations</strong>:</p>\r\n\r\n<ul>\r\n	<li>Aceh</li>\r\n	<li>Bali</li>\r\n	<li>Banten</li>\r\n	<li>Jakarta</li>\r\n	<li>Jambi</li>\r\n	<li>Jawa Barat</li>\r\n	<li>Jawa Tengah</li>\r\n	<li>Jawa Timur</li>\r\n	<li>Kalimantan Barat</li>\r\n	<li>Kalimantan Timur</li>\r\n	<li>Kepulauan Riau</li>\r\n	<li>Lampung</li>\r\n	<li>Maluku</li>\r\n	<li>Maluku Utara</li>\r\n	<li>Nusa Tenggara Barat</li>\r\n	<li>Nusa Tenggara Timur</li>\r\n	<li>Papua</li>\r\n	<li>Papua Barat</li>\r\n	<li>Riau</li>\r\n	<li>Sulawesi Selatan</li>\r\n	<li>Sulawesi Tengah</li>\r\n	<li>Sulawesi Tenggara</li>\r\n	<li>Sulawesi Utara</li>\r\n	<li>Sumatera Barat</li>\r\n	<li>Sumatera Selatan</li>\r\n	<li>Sumatera Utara</li>\r\n	<li>Yogyakarta</li>\r\n</ul>\r\n\r\n<p><strong>Sustainable Development Goals&nbsp;(SDGs) covered</strong>:</p>\r\n\r\n<ul>\r\n	<li>No Poverty&nbsp;(#1)</li>\r\n	<li>Good Health and Well-Being&nbsp;(#3)</li>\r\n	<li>Quality Education (#4)</li>\r\n	<li>Gender Equality (#5)</li>\r\n	<li>Clean Water and Sanitation&nbsp;(#6)</li>\r\n	<li>Decent Work and Economic Growth&nbsp;(#8)</li>\r\n	<li>Reduced Inequalities&nbsp;(#10)</li>\r\n	<li>Sustainable Cities and Communities&nbsp;(#11)</li>\r\n	<li>Climate Action&nbsp;(#13)</li>\r\n	<li>Peace and Justice Strong Institutions&nbsp;(#16)</li>\r\n</ul>\r\n\r\n<p>Partnerships for the Goals&nbsp;(#17)</p>\r\n', 'yayasan-tifa', 'N'),
(13, 'Tanoto Foundation', 'TanFou337', '8N69i5k+5pGOjk3SV66NmB2dq6wUTT8gVxUWjrlZC29M39d9JzwcQWKTSJt5g4OebA5hrCKvyzTy5Ab7KquY3g==', 'Sihol Aritonang', 'Sihol Aritonang', 'Executive Director', 'dimas_purnama@tanotofoundation.org', 'Jl. MH Thamrin no 31 – 32 Jakarta 10230', '11111111', 'Q4lwWcOR5HlV1JE', 'FsJA03NT2CcZDWN', '2016-10-24 13:19:46', 'Active', '', '0000-00-00 00:00:00', 'f87eb9ac3a84de6d886332891e4581b9.jpg', '', '', '', 'tanoto-foundation', 'N'),
(14, 'Yayasan SUKMA', 'YaySUK100', 'ezJ9rZ7gyV8Zy1TI0S16MBuV2h/4U4AHyy5Yw/mAW9frBSDDUUPXpHN6EauF17+2REolF/e8ALA3RPaSP8ZY4A==', 'Rerie L. Moerdijat', 'Melvina Eunice', '-', 'meunice@mediagroup.co.id', 'Jl. Pilar Mas Raya Kav. A-D, Kedoya, Kebun Jeruk, Jakarta Barat 11520', '02158300077', 'Q4lwWcOR5HlV1JE', 'FsJA03NT2CcZDWN', '2016-10-24 13:20:22', 'Active', '', '0000-00-00 00:00:00', 'd5f9a40634ab384a77c7e7aa0b453524.jpg', '', '<p><a href=\"http://www.yayasan-sukma.org/\" target=\"_blank\">Yayasan Sukma</a> didirikan di Jakarta pada Februari 2005 sebagai tanggapan terhadap bencana alam yang dipicu oleh gempa kuat dan tsunami yang melanda Aceh dan Sumatera Utara. Yayasan Sukma bekerja untuk mendukung pencapaian Tujuan Pembangunan Milenium (MDG) dan kemajuan Kemampuan Manusia dan Sumber Daya Manusia Investasi melalui&nbsp;Pendidikan. Pentingnya pendidikan adalah fokus utama kami terutama&nbsp;bagaimana membangun kembali Aceh. Memberikan kesempatan bagi anak-anak untuk belajar dan membangun masa depan mereka berarti kita membangun kembali Aceh. Pelatihan guru dan pertukaran antara sekolah membantu guru untuk memahami kebutuhan siswa, mengalami tantangan yang mereka hadapi pada kasus-kasus yang berbeda dan mengembangkan guru yang berkualitas.</p>\n\n<p><strong>Jenis Organisasi</strong>: Implementing / Pelaksana program (menggalang dana/hibah dari pihak lain untuk pendanaan program organisasi)</p>\n\n<p><strong>Bidang/Area Program</strong>: Pendidikan&nbsp;(SD, SMP, SMA)</p>\n\n<p><strong>Mitra dalam penyaluran hibah atau pelaksanaan program</strong>:&nbsp;Organisasi Pendidikan</p>\n\n<p><strong>Cakupan Program</strong>: Nasional</p>\n\n<p><strong>Lokasi Program</strong>: Aceh (Kota Lhokseumawe, Kab. Bireuen, Kab. Pidie)</p>\n\n<p><strong>Cakupan Tujuan Pembangunan Berkelanjutan&nbsp;(SDGs)</strong>:</p>\n\n<p>#4 Pendidikan Berkualitas</p>\n', '<p><a href=\"http://www.yayasan-sukma.org/\" target=\"_blank\">Sukma Foundation</a>&nbsp;established in Jakarta in February 2005 in response to the aftermath of the natural disasters triggered by the powerful earthquake and tsunami that struck Aceh and North Sumatera.&nbsp;Sukma Foundation works to support the achievement of the&nbsp;Millennium Development Goals (MDG)&nbsp;and the advancement of&nbsp;Human Capability and Human Resources Investment though Education.&nbsp;The importance of education is our main focus on how to rebuild Aceh. Giving chance for the children to study and build their future, meaning we are re-build Aceh as well. Teacher training and exchange between the schools helping teacher to understand The needs of the students, experience the challenge they face on different cases and developing qualified teachers.</p>\n\n<p><strong>Organization Type</strong>: Implementing (menggalang dana/hibah dari pihak lain untuk pendanaan program organisasi)</p>\n\n<p><strong>Program Area</strong>: Education&nbsp;(Elementary, middle school and high school)</p>\n\n<p><strong>Partners</strong>:&nbsp;Educational institutions</p>\n\n<p><strong>Program Scope</strong>: National</p>\n\n<p><strong>Program Location</strong>: Aceh (Kota Lhokseumawe, Kab. Bireuen, Kab. Pidie)</p>\n\n<p><strong>Sustainable Development Goals&nbsp;(SDGs) covered</strong>:</p>\n\n<p>#4 Quality Education</p>\n', 'yayasan-sukma', 'N'),
(15, 'Yayasan Keanekaragaman Hayati Indonesia (KEHATI)', 'YayKeaHay217', 'hbWvcwcHG+epIQek+9GlLvvWzEvnaNTi10KURoCT4hpMKW6QY4Gs+gX3LdUQCv5ln9F1+3hOhxDOQC7IDG5euQ==', 'M.S. Sembiring', 'M.S. Sembiring', 'Direktur', 'sembiring@kehati.or.id', 'Jl. Bangka VIII No. 3B Pela Mampang Jakarta 12720', '0217183185', 'Q4lwWcOR5HlV1JE', 'FsJA03NT2CcZDWN', '2016-10-24 13:21:01', 'Active', '', '0000-00-00 00:00:00', '71e5c5e163d3cc77bc751d4ee9ef4e8e.jpg', '', '<p>Kehadiran&nbsp;<a href=\"http://www.kehati.or.id/en/\" target=\"_blank\">Yayasan Keanekaragaman Hayati Indonesia (KEHATI)</a>&nbsp;sejak 12 Januari 1994 dimaksudkan untuk menghimpun dan mengelola sumberdaya yang selanjutnya disalurkan dalam bentuk dana hibah, fasilitasi, konsultasi dan berbagai fasilitas lain guna menunjang berbagai program pelestarian keanekaragaman hayati Indonesia dan pemanfaatannya secara adil dan berkelanjutan.</p>\n\n<p>Keberadaan Yayasan KEHATI tidak terlepas dari wujud pelaksanaan KTT Bumi di Rio de Janeiro tahun 1992 dan Deklarasi Tokyo tahun 1993 dimana pimpinan tiga negara yaitu Amerika Serikat, Jepang dan Indonesia sepakat bekerjasama untuk membantu program pelestarian keanekaragaman hayati secara berkelanjutan di Indonesia. Emil Salim, yang pada tahun 1993 telah selesai menjabat sebagai Menteri Negara Kependudukan dan Lingkungan Hidup, diminta oleh Pemerintah Indonesia untuk membentuk organisasi non-pemerintah sebagai penampung dan pelaksana program keanekaragaman hayati dengan bantuan hibah dari Pemerintah Amerika Serikat. Dengan bantuan rekan-rekan tokoh organisasi non-pemerintah, seperti Koesnadi Hardjasomantri, Ismid, Hadad, Erna Witoelar, M.S. Kismadi, dan Nono Anwar Makarim, disusunlah anggaran dasar, organisasi dan program kerja Yayasan Keanekaragaman Hayati Indonesia, disingkat KEHATI.</p>\n\n<p>Hibah yang diperoleh dari Amerika Serikat ini berbentuk dana cadangan abadi (endowment fund). Dana abadi ini diputar di pasar modal dalam bentuk saham dan obligasi. Hasil dari perputaran dana ini diperoleh imbalan yang dipakai untuk membiayai program bantuan hibah yang dilaksanakan oleh mitra KEHATI. Bantuan KEHATI dapat berbentuk dana hibah, tenaga ahli, konsultasi dan berbagai bentuk fasilitasi bagi kegiatan LSM, KSM, lembaga penelitian, pendidikan &amp; pelatihan serta berbagai organisasi dan komponen masyarakat madani yang memiliki program dan kegiatan sejalan dengan program KEHATI, pelestarian dan pemanfaatan keanekaragaman hayati secara adil dan berkelanjutan.</p>\n\n<p>KEHATI bertindak sebagai katalisator untuk menemukan cara-cara inovatif dalam melestarikan, mengelola dan memanfaatkan keanekaragaman hayati Indonesia secara berkelanjutan. Berbagai bentuk kerjasama dijalin dengan lembaga-lembaga yang dapat mendukung visi dan misi KEHATI, seperti pemerintah pusat dan daerah, komunitas bisnis, perguruan tinggi, LSM/KSM, asosiasi profesi, maupun media massa. Dukungan berbagai pihak tersebut di atas sangat diperlukan untuk menyelamatkan keanekaragaman hayati Indonesia dari kemusnahan, sehingga manfaat dari kelestarian keanekaragaman hayati dapat dirasakan hingga generasi penerus kelak.</p>\n\n<p><strong>Jenis organisasi:</strong>&nbsp;Grant maker/Pemberi hibah (memiliki sumber dana sendiri dan menyalurkannya ke organisasi mitra atau langsung ke masyarakat)</p>\n\n<p><strong>Bidang/Area Program</strong>:&nbsp;Konservasi dan sustainable use of Indonesian bidoversity</p>\n\n<p><strong>Program Utama</strong>:</p>\n\n<ul>\n	<li>Tropical Forest Conservation Action - Sumatera</li>\n	<li>Tropical Forest Conservation Action - Kalimantan</li>\n	<li>Pengelolaan Sumber Daya Alam berbasis Masyarakat (PSDABM) MCA-Indonesia</li>\n	<li>Green Corridor Initiatives</li>\n	<li>Sorghum</li>\n</ul>\n\n<p><strong>Cakupan Program</strong>: Nasional</p>\n\n<p>&nbsp;</p>\n\n<p><strong>Lokasi Program:</strong></p>\n\n<ul>\n	<li>Aceh</li>\n	<li>Bengkulu</li>\n	<li>Jakarta</li>\n	<li>Riau</li>\n	<li>Papua Barat</li>\n	<li>Nusa Tenggara Barat</li>\n	<li>Lampung</li>\n	<li>Kepulauan Riau</li>\n	<li>Kalimantan Utara</li>\n	<li>Kalimantan Timur</li>\n	<li>Kalimantan Barat</li>\n	<li>Jawa Timur</li>\n	<li>Jawa Tengah</li>\n	<li>Jawa Barat</li>\n	<li>Jambi</li>\n</ul>\n\n<p><strong>Cakupan Tujuan Pembangunan Berkelanjutan (SDGs):</strong></p>\n\n<ul>\n	<li>Kemitraan untuk Mencapai Tujuan (#17)</li>\n	<li>Ekosistem Daratan (#15)</li>\n	<li>Ekosistem Lautan (#14)</li>\n	<li>Penanganan Perubahan Iklim (#13)</li>\n	<li>Pekerjaan Layak dan Pertumbuhan Ekonomi (#8)</li>\n	<li>Energi Bersih dan Terjangkau (#7)</li>\n	<li>Air Bersih dan Sanitasi Layak (#6)</li>\n	<li>Kehidupan Sehat dan Sejahtera (#3)</li>\n</ul>\n', '<p>The presence of the Indonesian Biodiversity Foundation (KEHATI) since January 12, 1994 is intended to collect and manage resources that subsequently distributed in the form of grants, facilitation, consulting and various other facilities to support various Indonesia&#39;s biodiversity programs and its fair and sustainable utilization.</p>\n\n<p>The existence KEHATI Foundation is the implementation of&nbsp;the Earth Summit in Rio de Janeiro in 1992 and the Tokyo Declaration of 1993 in which the leaders of the three countries, namely the United States, Japan and Indonesia agreed to work together to help the program in a sustainable biodiversity conservation in Indonesia. Emil Salim, who in 1993 had finished his period as&nbsp;the Minister of State for Population and the Environment, requested by the Indonesian government to establish a non-governmental organization as a placeholder and implementer of biodiversity programs with a grant from the United States government. With the help of fellow leaders of non-governmental organizations, such as Koesnadi Hardjasomantri, Ismid Hadad, Erna Witoelar, M.S. Kismadi, and Nono Anwar Makarim, they drafted statutes, organizational and work programs of the Indonesian Biodiversity Foundation, or&nbsp;KEHATI.</p>\n\n<p>The grant from the United States was in a form of&nbsp;endowment fund. The endowment was invested&nbsp;in the capital market in the form of stocks and bonds. The results of this cash flow is obtained and&nbsp;used to finance the grant programs implemented by partners of KEHATI. Aid&nbsp;from&nbsp;KEHATI can take form in grants, experts, consulting and various forms of facilitation for the activities of NGOs, CSOs, research institutions, education and training as well as various organizations and components of civil society that have programs and activities in line with KEHATI, conservation and equitable and sustainable use of biodiversity.</p>\n\n<p>KEHATI act as a catalyst to finding innovative ways to conserve, manage and use sustainable biodiversity in Indonesia. Various forms of partnerships&nbsp;forged with institutions that can support the vision and mission of KEHATI, such as central and local government, the business community, universities, NGOs / CSOs, professional associations, and the mass media. Support from various parties mentioned above is needed to save Indonesian biodiversity from extinction, so that the benefits of conservation of biodiversity can be felt for the next generation in the future.</p>\n\n<p><strong>Organization Type:</strong>&nbsp;Grant maker (have their own funding sources and distribute it to partner organizations or directly to the public)</p>\n\n<p><strong>Program Area</strong>: Conservation and&nbsp;sustainable use of Indonesian biodversity</p>\n\n<p><strong>Main Programs</strong>:</p>\n\n<ul>\n	<li>Tropical Forest Conservation Action - Sumatera</li>\n	<li>Tropical Forest Conservation Action - Kalimantan</li>\n	<li>Natural Resources-based Community Management (PSDABM) MCA-Indonesia</li>\n	<li>Green Corridor Initiatives</li>\n	<li>Sorghum</li>\n</ul>\n\n<p><strong>Program Scope</strong>: National</p>\n\n<p><strong>Program Locations:</strong></p>\n\n<ul>\n	<li>Aceh</li>\n	<li>Bengkulu</li>\n	<li>Jakarta</li>\n	<li>Riau</li>\n	<li>Papua Barat</li>\n	<li>Nusa Tenggara Barat</li>\n	<li>Lampung</li>\n	<li>Kepulauan Riau</li>\n	<li>Kalimantan Utara</li>\n	<li>Kalimantan Timur</li>\n	<li>Kalimantan Barat</li>\n	<li>Jawa Timur</li>\n	<li>Jawa Tengah</li>\n	<li>Jawa Barat</li>\n	<li>Jambi</li>\n</ul>\n\n<p><strong>Sustainable Development Goals&nbsp;(SDGs) covered:</strong></p>\n\n<ul>\n	<li>Good Health and Well-being&nbsp;(#3)</li>\n	<li>Clean Water and Sanitation (#6)</li>\n	<li>Affordable and Clean Energy (#7)</li>\n	<li>Decent Work and Economic Growth (#8)</li>\n	<li>Climate Action (#13)</li>\n	<li>Life Below Water (#14)</li>\n	<li>Life on Land (#15)</li>\n	<li>Partnerships for the Goals (#17)</li>\n</ul>\n', 'yayasan-keanekaragaman-hayati-indonesia-kehati-', 'N'),
(16, 'LAZISMU', 'LAZ786', 'J38OTKDKma7Ww3nvQt0DXqSpQHqZd3pkSeaAYiFFQuXKKsVnH6USGU+H3+yjddr1HZaBv0W1CXCPUpYjIoHoUg==', 'Andar Nubowo, DEA', 'Edi Suryanto', 'Corporate Relationship Director', 'surya1541@gmail.com', 'Gd. Pusat Dakwah Muhammadiyah Jl. Menteng Raya 62 Jakarta Pusat 10340', '0213150400', 'Q4lwWcOR5HlV1JE', 'FsJA03NT2CcZDWN', '2016-10-24 13:21:51', 'Active', '', '0000-00-00 00:00:00', '83b3aff9eb9a63e6efd0b36f209e357d.jpg', '', '<p>Lazis-Muhammadiyah (Lazismu) adalah lembaga zakat tingkat nasional yang berkhidmat dalam pemberdayaan masyarakat melalui pendayagunaan secara produktif dana zakat, infaq, wakaf dan kedermawanan lain baik dari perseorangan, lembaga, perusahaan dan instansi lainnya. Program Pendayagunaan Lazismu berfokus pada; Pemberdayaan Ekonomi, Pendidikan, Sosial dan Dakwah. Dalam operasional programnya, LAZISMU didukung oleh jaringan konsolidasi lembaga zakat yang tersebar di seluruh propinsi sehingga program-program pendayagunaan LAZISMU mampu menjangkau seluruh wilayah Indonesia secara cepat, terfokus dan tepat sasaran.</p>\n\n<p>Jenis organisasi: Implementing/Pelaksana program (menggalang dana/hibah dari pihak lain untuk pendanaan program organisasi)</p>\n\n<p>Bidang/area program: Ekonomi, pendidikan, kesehatan, lingkungan, kemanusiaan, dakwah</p>\n\n<p>Program utama:</p>\n\n<ul>\n	<li>Program 1 Indonesia Terang, elektrifikasi</li>\n	<li>Program 2 Trensains (Pesantren Sains)</li>\n	<li>Program 3 Sejuta UMKM</li>\n	<li>Program 4 Tani Bangkit</li>\n	<li>Program 5 Tanggap Darurat Bencana</li>\n</ul>\n\n<p>Cakupan program: Nasional &amp; Internasional</p>\n\n<p>Lokasi program:</p>\n\n<ul>\n	<li>Jawa Barat</li>\n	<li>Jawa Tengah</li>\n	<li>Jawa Timur</li>\n	<li>Kalimantan Barat</li>\n	<li>Kalimantan Selatan</li>\n	<li>Kalimantan Tengah</li>\n	<li>Kalimantan Timur</li>\n	<li>Kalimantan Utara</li>\n	<li>Kepulauan Bangka Belitung</li>\n	<li>Kepulauan Riau</li>\n	<li>Lampung</li>\n	<li>Maluku</li>\n	<li>Nusa Tenggara Barat</li>\n	<li>Nusa Tenggara Timur</li>\n	<li>Papua</li>\n	<li>Riau</li>\n	<li>Sulawesi Barat</li>\n	<li>Sulawesi Selatan</li>\n	<li>Sulawesi Tengah</li>\n	<li>Sulawesi Tenggara</li>\n	<li>Sumatera Barat</li>\n	<li>Sumatera Selatan</li>\n	<li>Sumatera Utara</li>\n	<li>Yogyakarta</li>\n	<li>Jambi</li>\n	<li>Jakarta</li>\n	<li>Gorontalo</li>\n	<li>Bengkulu</li>\n	<li>Banten</li>\n	<li>Aceh</li>\n</ul>\n\n<p>Mitra:</p>\n\n<ul>\n	<li>Organisasi Kemasyarakatan (Ormas)</li>\n	<li>NGO/LSM</li>\n</ul>\n\n<p>Cakupan Tujuan Pembangunan Berkelanjutan (SDGs):</p>\n\n<ul>\n	<li>Kemitraan untuk Mencapai Tujuan</li>\n	<li>Perdamaian, Keadilan, dan Kelembagaan yang Tangguh</li>\n	<li>Konsumsi dan Produksi yang Bertanggung Jawab</li>\n	<li>Berkurangnya Kesenjangan</li>\n	<li>Industri, Inovasi, dan Infrastruktur</li>\n	<li>Pekerjaan Layak dan Pertumbuhan Ekonomi</li>\n	<li>Energi Bersih dan Terjangkau</li>\n	<li>Air Bersih dan Sanitasi Layak</li>\n	<li>Kesetaraan Gender</li>\n	<li>Pendidikan Berkualitas</li>\n	<li>Kehidupan Sehat dan Sejahtera</li>\n	<li>Tanpa&nbsp;Kelaparan</li>\n	<li>Tanpa&nbsp;Kemiskinan</li>\n</ul>\n', '', 'lazismu', 'N'),
(17, 'Yayasan Belantara', 'YayBel605', 'G7M/Jvx5tlgC4qArlM0/a9UH33gFZPYlBCYNkfWQRf0N0ACHBD/pXJ+pXoeiRJ1j4zvN8sOUVvsVzGrPy/5cjw==', 'Agus Sari', 'Natalia Simanjuntak', 'Special Assistant to CEO', 'natalia.simanjuntak@belantara.or.id', '3rd Floor, Boutique Office, Jl. Timor No. 6, Menteng, Jakarta 10350, Indonesia', '083808476930', 'Q4lwWcOR5HlV1JE', 'FsJA03NT2CcZDWN', '2016-10-24 13:22:39', 'Active', '', '0000-00-00 00:00:00', '342607b3b9b1050419ce6fdfc223045c.jpg', '', '', '', 'yayasan-belantara', 'N'),
(18, 'Plan International Indonesia', 'PlaIntInd823', '0HPUdZtUh5i6U+bfXKCskSkO8hT2o9UKI8ED0/AsSGR0XtjBMzF6hdVKiCcwhV2uKvNJ9z7AlWIBNJxdqSUVEA==', 'Myrna Remata Evora', 'Ilyana Habsjah', 'Corporate and Private Sector Engagement', 'ilyana.habsjah@plan-international.org', 'Menara Duta Lt.2 – Jl HR Rasuna Said Kav B9, Kuningan, Jakarta Selatan 12910', '0215229566', 'Q4lwWcOR5HlV1JE', 'FsJA03NT2CcZDWN', '2016-10-24 13:23:25', 'Active', '', '0000-00-00 00:00:00', 'cba0f5e41a82e2e82a26261c5f14d39c.png', '', '', '', 'plan-international-indonesia', 'N');
